      <option value="<?php echo $month->year . '-' . $month->month; ?>" <?php if (isset($_GET['month']) && $_GET['month'] == $month->year . '-' . $month->month) echo 'selected'; ?>><?php echo date('F Y', strtotime($month->year . '-' . $month->month . '-01')); ?></option>
      <?php endforeach; ?>
