<div class="w-[80%] max-w-[1360px] gap-16 m-auto mt-[50px] post-container grid grid-cols-1 lg:grid-cols-2">

<?php
  if (isset($_GET['month'])) {
    $month = $_GET['month'];
    if ($month != 'all') {
      $year = substr($month, 0, 4);
      $month = substr($month, 5, 2);
      $args = array(
        'post_type' => 'post',
        'post_status' => 'publish',
        'posts_per_page' => 10,
        'year' => $year,
        'monthnum' => $month
      );
    } else {
      $args = array(
        'post_type' => 'post',
        'post_status' => 'publish',
        'posts_per_page' => 10,
      );
    }
  } else {
    $args = array(
      'post_type' => 'post',
      'post_status' => 'publish',
      'posts_per_page' => 10,
    );
  }
  
  // Custom query. 
  $query = new WP_Query( $args );
  // Check that we have query results. 
  if ( $query->have_posts() ) {
      // Start looping over the query results. 
      while ( $query->have_posts() ) {
          $query->the_post();
          ?>
        <div class="flex max-w-[100%] m-auto flex-col gap-4 justify-center items-center mb-[50px] text-center">
        <div class="project-featured" style="background-image: url('<?php echo get_the_post_thumbnail_url(); ?>');display:block;height:350px;width:100%;background-repeat:no-repeat;background-size:cover;background-position: center;"></div>
          <h3 class="glacial-bold text-left text-[20px]"><?php echo get_the_title(); ?></h3>
          <p class="text-left mb-[30px]"><?php echo wp_trim_words(get_the_excerpt(), 30) . '...' ?></p>
          <div class="content-wrapper px-[20px] w-full gap-4 flex justify-between items-center">
            <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="92" height="92" viewBox="0 0 92 92">
              <defs>
                <pattern id="pattern" width="1" height="1" viewBox="10.116 11.528 24.112 24.112">
                  <image preserveAspectRatio="xMidYMid slice" width="167.273" height="92" xlink:href="data:image/png;base64,"/>
                </pattern>
              </defs>
              <circle id="logo" cx="46" cy="46" r="46" fill="url(#pattern)"/>
            </svg>
